package allTest;import java.util.Arrays;/** * 字符串全排列问题 */public class AllPosibileString {    public static void main(String [] args){        char[] cahrs = {'a','b','c'};        allResult(cahrs,0,2);    }    public static void allResult(char[] chars , int start , int end){        if(start == end){            System.out.println(Arrays.toString(chars));        }else{            for(int i=start; i<= end ; i++){                swap(chars,i,start);                allResult(chars,start+1,end);                swap(chars,i,start);            }        }    }    public static void swap(char[] chars , int a , int b){        char temp = chars[a];        chars[a] = chars[b];        chars[b] = temp;    }}